% specnoise.m plot the spectrum of a noise signala = 1;                              % upper range = 1b = -1;                             % lower range = -1time = 1;                           % length of time Ts = 1/10000;                       % time interval between samplesx = (b-a) .* rand(1,time/Ts) + a;   % Ts points of noise for time secondsplotspec(x,Ts)                      % call plotspec to draw spectrum