% speccos.m plot the spectrum of a cosine wavetime=2;                     % length of time Ts=1/100;                   % time interval between samplest=Ts:Ts:time;               % create a time vectorx1=cos(2*pi*10*t+phi);      % create cos wavex2=cos(2*pi*18*t+phi);      % create cos wavex3=cos(2*pi*33*t+phi);      % create cos wavex = x1+0.5*x2+2*x3;         % add cos wavesplotspec(x,Ts)              % draw waveform and spectrum% This problem shows linearity within the system of fourier transforms. % The sum of the functions will in turn display the sum of the fourier% transforms.